// Contributor: James Kim
// Source: NA
// References
// Reviewer: 
//
// Description: This rule detects a service account sign-in to a device that has not previously been signed into in a 30d period.
// Handling Guidance: 
//
// Query
let reference = DeviceLogonEvents
| where TimeGenerated > ago(30d) and TimeGenerated < ago(1d)
| where ActionType == "LogonSuccess"
| where LogonType in ("Interactive","RemoteInteractive")
| where AccountName startswith "svc" or AccountName contains "service"
| summarize
    ['30d Local Admin Distinct Device Count']=dcountif(DeviceName,IsLocalAdmin == "true"),
    ['30d Local Admin Device List']=make_set_if(DeviceName, IsLocalAdmin == "true")
    by AccountName
| sort by ['30d Local Admin Distinct Device Count'] desc;
//comparison
let unique = DeviceLogonEvents
| where TimeGenerated > ago(2d) and TimeGenerated < ago(1d)
| where ActionType == "LogonSuccess"
| where LogonType in ("Interactive","RemoteInteractive")
| where AccountName startswith "svc" or AccountName contains "service"
| summarize
    ['Devices Accessed']=dcountif(DeviceName,IsLocalAdmin == "true"),
    ['Device Access List']=make_set_if(DeviceName, IsLocalAdmin == "true")
    by AccountName
| join kind=leftanti reference on AccountName
| where ['Devices Accessed'] > 0
| mv-expand ['Device Access List']
| extend DeviceName = tostring(['Device Access List']);
//Details of device signins
DeviceLogonEvents
| where TimeGenerated > ago(2d) and TimeGenerated < ago(1d)
| join unique on AccountName, DeviceName
| project TimeGenerated, ActionType, LogonType, IsLocalAdmin, ['Devices Accessed'], ['Device Access List'], AccountName, DeviceName, MachineGroup, InitiatingProcessAccountName, InitiatingProcessCommandLine
