properties.query:
// Contributor: Anthony Edelstein
// Reviewer: 
// Reference:https://github.com/reprise99/Sentinel-Queries/blob/7854470ea86ddbe1c68b0fd499d7d27b84598732/Azure%20Active%20Directory/Audit-DetectConditionalAccessChangesAfterHours.kql
//
// Description: This query identifies changes made to a conditional access policy.
// Handling Guidance: Use the workbook "Conditional Access Change Workbook" to determine what has changed. Find the PCN in Jira if applicable.
//
// Date     | Author                             | Change notes
// ---------------------------------------------------
// 04/03/2023 | Anthony Edelstein | 1.0 
// 08/03/2023 | Adrian Shina | Added handling guidance
// Query
AuditLogs
| where OperationName has "conditional access"
| extend ['Conditional Access Policy Name'] = tostring(TargetResources[0].displayName)
| extend Actor = tostring(parse_json(tostring(InitiatedBy.user)).userPrincipalName)
| extend Description_ = tostring(parse_json(tostring(parse_json(tostring(parse_json(tostring(parse_json(tostring(TargetResources[0].modifiedProperties))[0].newValue)).grantControls)).authenticationStrength)).description)
| extend NewPolicyValue = tostring(parse_json(tostring(parse_json(tostring(TargetResources[0].modifiedProperties))[0].newValue)))
| extend OldPolicyValue = tostring(parse_json(tostring(parse_json(tostring(TargetResources[0].modifiedProperties))[0].oldValue)))
| extend Changes = bag_pack(NewPolicyValue,1)
| project 
    OperationName, 
    ['Conditional Access Policy Name'], 
    Actor,
    Description_,
    Result,
    NewPolicyValue,
    OldPolicyValue,
    Changes
