properties.query:
// Contributor: 
// Reviewer: 
// Reference:
//
// Description: This detection addresses most of the known ways to utilize this binary for malicious/unintended purposes. It attempts to accommodate for most detection evasion techniques, like commandline obfuscation and binary renaming.
// Handling Guidance: 
//
// Date     | Author             | Change notes
// ---------------------------------------------------
//          | aedelstein         | Added entity mapping to existing rule
// 03/15/23 | gabbot             | Excluded chrome_crashpad_handler
// 05/20/25 | ltrainer           | excluded winatp.cer to eliminate FPs from MDE client analyzer
// 01/22/25 | ecralev            | excluded SenseIR.exe as InitiatingProcessParentFileName for Live Response script to retrieve browser history.
//
// Set the time span for the query.
let Timeframe = 1h;
// Set the HashTimeframe for the hash lookup; longer makes it more accurate, but obviously also more resource-intensive.
let HashTimeframe = 14d;
// Get all known SHA1 hashes for certutil executions or renamed files formerly named certutil.
let CertUtilPESha1=materialize(DeviceProcessEvents
    | where Timestamp > ago(HashTimeframe)
    | where ProcessVersionInfoOriginalFileName == "CertUtil.exe"
    | where isnotempty(SHA1)
    | summarize sha1=make_set(SHA1));
let CertUtilFESha1=materialize(DeviceFileEvents
    | where Timestamp > ago(HashTimeframe)
    | where PreviousFileName contains "certutil" or FileName contains "certutil"
    | where isnotempty(SHA1)
    | summarize sha1=make_set(SHA1));
DeviceProcessEvents
| where Timestamp >= ago(Timeframe)
// Get all executions by processes with a SHA1 hash that is or was named certutil.
| where SHA1 in (CertUtilPESha1)
    or SHA1 in (CertUtilFESha1)
    or FileName =~ "certutil.exe"
    or ProcessCommandLine has "certutil"
// Exclude noisy false positive - chrome_crashpad_handler
| where ProcessCommandLine !startswith_cs "/opt/google/chrome/chrome_crashpad_handler"
// Create a new field called CleanProcessCommandLine which gets populated with the value of ProcessCommandLine as Windows parses it for execution, 
// removing any potential command line obfuscation. 
| extend CleanProcessCommandLine=parse_command_line(ProcessCommandLine, "windows")
// Search for de-obfuscated commands used. 
// Urlcache is the documented attribute. However, url is also accepted.
// Verifyctl is the documented attribute. However, verify is also accepted.
| where CleanProcessCommandLine has_any ("decode", "encode", "verify", "url") 
| where not(ProcessCommandLine endswith "winatp.cer") //Exclude MDE Client Analyzer
| where InitiatingProcessParentFileName != "SenseIR.exe"
| sort by TimeGenerated desc
