properties.query:
// Contributor: Obadiah Bridges
// Source: NA
//References
// Reviewer: Mohamed Elgendy 
//
// Description: This rule detects when a local user account is created on a machine.
// Handling Guidance: Contact the indvidiual who created the account and determine what the business purpose of the account is for. Is this intentional activity? Is there a ticket associated with this activity that can be referenced?
//
// Query:
// For this query we are focused on Local Accounts created on workstations. 
let removedAccountNames_ = dynamic(["default", "ddagent", "ddagentuser", "lenovo", "devv", "defaultuser100000", "defaultuser100001"]);
//let removedDeviceNames_ = dynamic(["ushenpstrdc01.str.wwstar.com", "cg01dcusweb01.usweb.costar.local", "ushenpstrdc02.str.wwstar.com"]);
let removedAccountDomains_ = dynamic (["us","str0","usweb"]);
let removedMachineGroups_ = dynamic (["Domain Controllers", "Servers"]);
DeviceEvents 
//| where TimeGenerated > ago(30d)
| where ActionType == "UserAccountCreated"
| extend _AccountCreator = InitiatingProcessAccountName
| where not(MachineGroup has_any (removedMachineGroups_))
//| where not(DeviceName has_any (removedDeviceNames_)) 
| where not(AccountDomain has_any (removedAccountDomains_)) 
| where not(AccountName has_any (removedAccountNames_)) 
| where InitiatingProcessAccountSid <> "S-1-5-18"
| extend _AlertTitle = strcat("Local Account Creation on ", DeviceName)
| extend _AlertDescription = strcat("This rule detects when a local user account is created in the environment. Domain Controllers and other notable actors have been removed from the query. ")
| extend _HandlingGuidance = strcat("Contact the indvidiual who created the account and determine what the business purpose of the account is for. Is this intentional activity? Is there a ticket associated with this activity that can be referenced? Most of the time, this shouldn't be happening.")
| where _AccountCreator !startswith "svc"
// turns each JSON element from EventData into it's own column
//removes Domain names of "US" and "USWEB"
| extend CreatedAccountName=AccountName
| project
    TimeGenerated, 
    CreatedAccountName,
    AccountDomain,
    _AccountCreator,
    DeviceName,
    MachineGroup,
    _AlertTitle,
    _AlertDescription,
    _HandlingGuidance
