properties.query:
// Contributor: jkim2
// Reviewer: 
// Reference: 
//
// Description: This rule detects svc account enable/disable events being performed by another svc account. This may suggest a compromised service account gaining persistence on old disabled service accounts.
// Handling Guidance: Validate the activity with the owner of the service account and if confirmed as malicious, mark the account as compromised.
//
let Changes = IdentityDirectoryEvents
| where not(ActionType in("Account enabled", "Account disabled"))
| extend AdditionalFields = parse_json(AdditionalFields)
| extend FromGroup = AdditionalFields.["FROM.GROUP"]
| extend ToGroup = AdditionalFields.["TO.GROUP"]
| extend TargetObject = coalesce (
    AdditionalFields.["TARGET_OBJECT.USER"],
    AdditionalFields.["TARGET_OBJECT.DEVICE"],
    AdditionalFields.["TARGET_OBJECT.GROUP"]
)
| extend Hour = hourofday(TimeGenerated)
| project TimeGenerated, ActionType, AccountChanged = TargetAccountDisplayName, InitiatedBy = AccountName, FromGroup, ToGroup, DestinationDeviceName, Hour
| summarize Events = make_list(pack("TimeGenerated", TimeGenerated, "ActionType", ActionType, "AccountChanged", AccountChanged, "InitiatedBy", InitiatedBy, "FromGroup", FromGroup, "ToGroup", ToGroup, "DestinationDeviceName", DestinationDeviceName)) by Hour, AccountChanged;
IdentityDirectoryEvents
| where ActionType in("Account enabled", "Account disabled")
| where AccountName !has "svcAD_ManageUser" //this is the typical svc account for managing other svc accounts
| where TargetAccountDisplayName startswith "svc"
| where AccountName startswith "svc"
// | where TargetAccountDisplayName == "svcVRealizeAuto" //activity from red team test 4/11
| extend Hour = hourofday(TimeGenerated)
| project TimeGenerated, ActionType, Application, AccountChanged = TargetAccountDisplayName, InitiatedBy = AccountName, Hour
| join Changes on AccountChanged, Hour
| project TimeGenerated, ActionType, Application, AccountChanged, InitiatedBy, Events
