properties.query:
// Contributor: Sky Tran
// Reviewer: Nixy Camacho & Evgeniy Cralev
//
// Description: Calls out the usage of AzureAiTM. This should NEVER be used by legitimate users and indicates successful adversary credential harvesting.
//
// Change notes
// ------------
// 06/23/25 Sky Tran - initial query
// 06/24/25 Sky Tran - separated email/UPN columns and fixed time bins
//
let AzureIPInfo = externaldata(changeNumber: int, cloud: string, values: dynamic)[h"https://stsecopsn01.blob.core.windows.net/watchlists/ServiceTags_Public_20240311.json"] with (format = 'multijson')
    | project-away changeNumber, cloud
    | mv-expand parse_json(values)
    | extend Name = values.name
    | extend AddressPrefixes = values.properties.addressPrefixes
    | extend Platform = values.properties.platform
    | summarize make_list(AddressPrefixes);
SigninLogs
| where DeviceDetail.trustType == ''
| where ipv4_is_in_any_range(IPAddress, toscalar(AzureIPInfo))
| extend ConfidenceScoreNetwork = iif(NetworkLocationDetails == '[]', 1.0, 0.1)
| extend ConfidenceScoreApp = iif(AppDisplayName =~ 'OfficeHome', 1.0, 0.75)
| extend ConfidenceScore = ConfidenceScoreApp * ConfidenceScoreNetwork
| where ConfidenceScore > 0.75
| distinct 
    TimeGenerated,
    UserPrincipalName,
    UserAgent,
    AppDisplayName,
    IPAddress,
    ConfidenceScore,
    RiskDetail
| extend TimeBin = format_datetime(bin(TimeGenerated, 15m), 'MM/dd/yyyy HH:mm:ss')
| summarize 
    Email = maxif(UserPrincipalName, UserPrincipalName contains "@"),
    UPN = maxif(UserPrincipalName, UserPrincipalName !contains "@"),
    TimeBins = min(TimeBin)
  by UserAgent, AppDisplayName, IPAddress
| project TimeBins, Email, UPN, UserAgent, AppDisplayName, IPAddress
